import "./ExpenseList.css"

export default function ExtenseList({ date, title, amount }) {
    return (
        <div className="expense-list">
            <div>
                {date.toISOString()}
            </div>
            <div className="expense-list__description">
                <h2>{title}</h2>
                <div className="expense-list__price">${amount}</div>
            </div>
        </div>
    )
}

