import "./ExpenseList.css"

export default function ExtenseList(props) {
    const month = props.date.toLocaleString('ua-EN', { month: 'long' });
    const day = props.date.toLocaleString('ua-EN', { day: 'numeric' });
    const year = props.date.getFullYear();
    

    // add dynamic date + clear code watch up 1
    return (
        <div className="expense-list"> 
            <div>
                <div> 
                    {month}
                </div>
                <div>
                    {day}
                </div>
                <div>
                    {year}
                </div>
            </div>
            <div className="expense-list__description">
                <h2>{props.title}</h2>
                <div className="expense-list__price">${props.amount}</div>
            </div>
        </div>
    )
}

