import React, {useState} from "react";

import './ExpenseForm.css'
export default function ExpenseForm() {

    const [newTitle, setNewTitle] = useState(' ')
    const [newAmount, setNewAmount] = useState(' ')
    const [newDate, setNewDate] = useState(' ')


    // const titleChangeHandler = (event) => {
    //     setNewTitle(event.target.value)
    // }

    // const amountChangeHandler = (event) => {
    //     setNewAmount(event.target.value)
    // }
    
    // const dateChangeHandler = (event) => {
    //     setNewDate(event.target.value)

        const inputCharger = (identifier, value) => {
            if (identifier === "title") {
                setNewTitle(value);
            } else if (identifier === "amount") {
                setNewAmount(value)
            } else {
                setNewDate(value)
            }
        }
    

    return (
        <form>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type="text"onChange={(event) => inputCharger('title', event.target.value)} />
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input type="number" min="0.01" step="0.01" onChange={(event) => inputCharger('amount', event.target.value)} />
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type="date" min="2018-01-01" max="2023-12-31" onChange={(event) => inputCharger('date', event.target.value)}/>
                </div>
            </div>
            <div className="new-expense__actions">
                <button type="submit">Add Expense</button>
            </div>
    </form>
    )
}
